    private void butSubmitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butSubmitActionPerformed
        
        DBAccess dBAccess = new DBAccess();
        // create a new Doctor object with info from textFields
        System.out.println(jComboBox1.getSelectedItem().toString().toLowerCase());
        int branchId = dBAccess.getBranchIdByName(jComboBox1.getSelectedItem().toString().toLowerCase());
        Doctor doctor = new Doctor(inputName.getText(), inputSpec.getText(), branchId);
        
        
        dBAccess.addDoctor(doctor);
        
        // closes the frame 
        this.dispose(); 
    }//GEN-LAST:event_butSubmitActionPerformed

