    private void fillCombo() {
        jComboBox1.removeAllItems();

        ArrayList<String> branchList = new DBAccess().getAllBranches();

        for (int i = 0; i < branchList.size(); i++) {
            jComboBox1.addItem(branchList.get(i));
        }
    }
