    private void fillCombo() {
        DBAccess db = new DBAccess();
        ArrayList<Blood> bloods = db.getAllBlood();
        
        comboBlood.removeAllItems();

        for (int i = 0; i < bloods.size(); i++) {
            comboBlood.addItem(bloods.get(i).getName());
        }
        
    }
