    private void butInfoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butInfoActionPerformed

        // get the ssn from the combo box
        int id = Integer.parseInt(comboSSN.getSelectedItem().toString());
        Doctor doctor = new DBAccess().getDoctorById(id);
        

        if (doctor != null) {
            inputName.setText(doctor.getName());
            inputSpec.setText(doctor.getSpec() + "");
            String branch = new DBAccess().getDoctorBranch(doctor.getBranchId());
            fillComboBranch();
            comboBranch.setSelectedItem(branch);
            inputName.setEnabled(true);
            comboBranch.setEnabled(true);
            inputSpec.setEnabled(true);
            butUpdate.setEnabled(true);
            butDelete.setEnabled(true);
        }
    }//GEN-LAST:event_butInfoActionPerformed

    private void butDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butDeleteActionPerformed
        int id = Integer.parseInt(comboSSN.getSelectedItem().toString());
        DBAccess dBAccess = new DBAccess();
        dBAccess.deleteDoctorById(id);
        inputName.setText("");
        inputSpec.setText("");
        comboBranch.setSelectedIndex(1);
        fillCombo();
    }//GEN-LAST:event_butDeleteActionPerformed

    private void butUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butUpdateActionPerformed
        // TODO add your handling code here:
        
        int id = Integer.parseInt(comboSSN.getSelectedItem().toString());
        Doctor doctor = new DBAccess().getDoctorById(id);
        if (doctor != null) {
            doctor.update(inputName.getText(), inputSpec.getText(), comboBranch.getSelectedItem().toString());
        }
        this.dispose();
    }//GEN-LAST:event_butUpdateActionPerformed

