    private void fillCombo() {

        comboSSN.removeAllItems();

        ArrayList<Integer> ssnList = new DBAccess().getAllDoctorId();

        for (int i = 0; i < ssnList.size(); i++) {
            comboSSN.addItem(ssnList.get(i));
        }
    }
    
    private void fillComboBranch() {
        comboBranch.removeAllItems();

        ArrayList<String> branchList = new DBAccess().getAllBranches();

        for (int i = 0; i < branchList.size(); i++) {
            comboBranch.addItem(branchList.get(i));
        }
    }

